def step(c):
    # print(c)
    match c:
        case 'F' :
            t.forward(lenstep)
            global R
            global G
            global B
            global L
            if R >= 250 :
                R=0
            if G >= 250:
                L=0
            if G <= 1:
                L=1
            if B >= 250:
                B=0
           # B +=1
            if L == 1:
                G =G+0.1
            else:
                G =G-0.1
          #  R +=1            
            t.color(R,int(G),B)
            # t.color(0,0,255)
            # t.color(0,255,0)
        case '-' :
            t.left(angle)

        case '+' :
            t.right(angle)
        case '[' :
            p=[]
            r.put(t.heading())
            p.append(t.xcor())
            p.append(t.ycor())
                        
            gsp.append(p)
            #print(p,  gsp.qsize(),"я здесь")
            # p.clear()
            # print(gsp)
        case ']' :
            p=[]            
            p=gsp.pop()
            t.up()
            t.goto(p)
            t.down()
            t.setheading(r.get())

